mapper.select:
select
from sys_user a left join sys_user_group b on a.id=b.user_id left join sys_ugroup c on c.id= b.ugroup_id left join sys_ugroup_role d on d.role_id=c.id left join sys_role e on e.id=d.role_id left join sys_role_perm f on f.role_id=e.id left join sys_perm g on g.id= f.perm_id
a.id=#{userId}

select c.id, c.ugroup_name,c.created as ug_created,c.updated as ug_updated, e.id as roleId,e.role_name as role_name,e.created as r_created,e.updated as r_updated, g.id as permId ,g.pval as pval,g.parent as parent,g.name as gname,g.type as gtype, g.leaf as leaf,g.created as p_created,g.updated as p_updated
from sys_ugroup c left join sys_ugroup_role d on d.role_id=c.id left join sys_role e on e.id=d.role_id left join sys_role_perm f on f.role_id=e.id left join sys_perm g on g.id= f.perm_id
c.id=#{id}

select e.id as roleId,e.role_name as role_name,e.created as r_created,e.updated as r_updated, g.id as permId ,g.pval as pval,g.parent as parent,g.name as gname,g.type as gtype, g.leaf as leaf,g.created as p_created,g.updated as p_updated
from sys_role e left join sys_role_perm f on f.role_id=e.id left join sys_perm g on g.id= f.perm_id
e.id=#{id}
